import { memo, useEffect, useState } from 'react';
import { List, Image, Tag, Empty } from 'antd';
import ProCard from '@ant-design/pro-card';
import { getFundManagerChangeInfo } from '../../service';
import styles from './index.less';
import './index.less';

function Manager({ fundId }: { fundId: string }) {
  const [contentItems, setContentItems] = useState<any[]>([]);

  useEffect(() => {
    (async () => {
      const result = await getFundManagerChangeInfo({ fundId });
      setContentItems(result || []);
    })();
  }, []);

  if (Array.isArray(contentItems) && contentItems.length === 0) {
    return <Empty image={Empty.PRESENTED_IMAGE_SIMPLE} />;
  }

  return (
    <List
      size="small"
      dataSource={contentItems}
      renderItem={(item) => {
        return (
          <ProCard
            className={styles['card-wrap']}
            size="small"
            title={
              <span className={styles['head-title']}>
                {item.workEndDate == '至今'
                  ? `${item.workBeginDate}至今`
                  : `${item.workBeginDate}~${item.workEndDate}`}
              </span>
            }
            // headerBordered={false}
            subTitle={
              <div className={styles['sub-title']}>
                <span>任职回报：{item.periodReturn || '--'}</span>
                <span>标准差：{item.standardDeviation || '--'}</span>
                <span>最大回撤：{item.drawDown || '--'}</span>
              </div>
            }
          >
            <div className={styles['list-wrap']}>
              <Image
                width={200}
                src={item.headLogoUrl || 'error'}
                fallback="data:image/png;base64,"
              />
              <div className={styles['content-right']}>
                <div className={styles['content-head']}>
                  <span className={styles['head-name']}>{item.fundManagerName}</span>
                  <span className={styles['head-subtitle']}>
                    <span>历任基金数：{item.productHistoryCount || '--'}</span>
                    <span>历任基金公司数：{item.companyHistoryCount || '--'}</span>
                    {/* <span>投资年限：{item.companyHistoryCount || '--'}</span> */}
                  </span>
                  {item.personalTag && <Tag color="red">{item.personalTag}</Tag>}
                </div>
                <div className={styles['content-text']}>{item.personalProfile}</div>
              </div>
            </div>
          </ProCard>
        );
      }}
    />
  );
}

export default memo(Manager);
